question.answer:
def print_sqrs_1_to_n(n):
    """ Print a display of the squares of all numbers from 1 to
        the given parameter n.
    """
    for i in range(1, n + 1):
        print("{0} * {0} = {1}".format(i, i * i))

def run_length_encode(nums):
    """Return the run-length-encoded representation of the
       list of ints in 'nums'.
    """
    result = []
    current = None
    count = 0
    for num in nums:
        if num == current:
            count += 1
        else:
            if current is not None:
               result.append((current, count))
            current = num
            count = 1
    if current is not None:
        result.append((current, count))
    return result

