// Program to read lines from standard input and print them in reverse
#include <stdio.h>

char buffer[81];

int readline() {
    // Read a line into global buffer. Return length of line or -1 if EOF.
    int c = 0;
    int i = 0;
    while ((c = getchar()) != '\n' && c != EOF) {
        buffer[i++] = c;
    }
    return i > 0 || c == '\n' ? i : -1;
}

int main() {
    // Read and print lines reversed
    int numRead = 0;
    numRead = readline();
    while (numRead >= 0) { // Print line backwards
        for (int i = numRead - 1; i >= 0; i--) {
            putchar(buffer[i]);
        }
        putchar('\n');
        numRead = readline();  // Next line
    }
}

