question.answer:
public class Bod {
    private String name;
    private int age;

    public Bod(String name, int age) {
        this.name = name;
        this.age = age;
    }

    public String getName() {
        return this.name;
    }

    public int getAge() {
        return this.age;
    }

    public void growOlder(int nYears) {
        this.age += nYears;
    }
}

